import React, { useState, useEffect } from "react";
import axios from "axios";

const API_BASE_URL = "http://localhost:3001/api/mobile";
const USER_API_URL = "http://localhost:3001/api/user";
// Placeholder for AI feature endpoint
const AI_SUGGESTION_API = "http://localhost:3001/api/suggest"; 

const initialFilters = {
  Brand: "",
  RAM: "",
  Front_Camera: "",
  Back_Camera: "",
  Processor: "",
  Launched_Year: "",
  min_Price: "",
  max_Price: "",
  min_Battery_Capacity: "",
  max_Battery_Capacity: "",
  min_Mobile_Weight: "",
  max_Mobile_Weight: "",
  min_Screen_Size: "",
  max_Screen_Size: "",
  Model: "",
  view: "all",
};

const MobileFilter = ({ userID, onLogout }) => {
  const [filters, setFilters] = useState(initialFilters);
  const [mobiles, setMobiles] = useState([]);
  const [filterOptions, setFilterOptions] = useState({});
  const [loading, setLoading] = useState(false);
  const [optionsLoading, setOptionsLoading] = useState(true);
  const [likedIds, setLikedIds] = useState(new Set());
  const [userName, setUserName] = useState("");
  const [suggestionLoading, setSuggestionLoading] = useState(false); // State for AI button loading


  useEffect(() => {
    const fetchUserName = async () => {
      if (!userID) {
        setUserName("Guest");
        return;
      }
      try {
        const res = await axios.get(`${USER_API_URL}`, { params: { userID } });
        setUserName(res.data?.name || "User");
      } catch {
        setUserName("User");
      }
    };
    fetchUserName();
  }, [userID]);

  const fetchLikedIds = async () => {
    if (!userID) return;
    try {
      const response = await axios.get(`${API_BASE_URL}/favorites`, {
        params: { userID },
      });
      setLikedIds(new Set(response.data.map((m) => m.id)));
    } catch (error) {
      console.error("Failed to fetch liked IDs:", error);
    }
  };

  useEffect(() => {
    const fetchOptions = async () => {
      try {
        const response = await axios.get(`${API_BASE_URL}/options`);
        setFilterOptions(response.data);
      } catch (error) {
        console.error("Error fetching filter options:", error);
      } finally {
        setOptionsLoading(false);
      }
    };
    fetchOptions();
    fetchLikedIds();
  }, [userID]);

  useEffect(() => {
    if (optionsLoading) return;
    const fetchData = async () => {
      setLoading(true);
      try {
        let response;
        if (filters.view === "favorites") {
          response = await axios.get(`${API_BASE_URL}/favorites`, {
            params: { userID },
          });
        } else {
          const { view, ...restFilters } = filters;
          const activeFilters = Object.fromEntries(
            Object.entries(restFilters).filter(([_, v]) => v !== "")
          );
          const queryParams = new URLSearchParams(activeFilters).toString();
          response = await axios.get(`${API_BASE_URL}/filter?${queryParams}`);
        }
        setMobiles(response.data);
      } catch (error) {
        console.error("Error fetching data:", error);
      } finally {
        setLoading(false);
      }
    };
    const handler = setTimeout(() => fetchData(), 300);
    return () => clearTimeout(handler);
  }, [filters, optionsLoading, userID]);

  const handleLikeToggle = async (mobileId) => {
    const isLiked = likedIds.has(mobileId);
    try {
      const response = await axios.post(`${API_BASE_URL}/like`, {
        mobileId,
        userID,
        isLiked: !isLiked,
      });
      if (response.data.success) {
        setLikedIds((prev) => {
          const newIds = new Set(prev);
          isLiked ? newIds.delete(mobileId) : newIds.add(mobileId);
          return newIds;
        });
        if (filters.view === "favorites") setFilters((prev) => ({ ...prev }));
      }
    } catch (error) {
      console.error("Toggle like failed:", error);
      alert("Failed to update like status.");
    }
  };

  const handleLogout = () => {
    if (onLogout) onLogout();
    else {
      localStorage.clear();
      window.location.reload();
    }
  };

  const handleAISummary = async () => {
    setSuggestionLoading(true);

    const { view, ...restFilters } = filters;
    const activeFilters = Object.fromEntries(
      Object.entries(restFilters).filter(([_, v]) => v !== "")
    );
    const prompt = `Generate a mobile recommendation report based on the following filters: ${JSON.stringify(activeFilters)}. Suggest top models that best match these preferences.`;

    try {
      const response = await axios.post(`${API_BASE_URL}/summary`, { prompt });
      alert(response.data.summary || "No summary returned.");
    } catch (error) {
      console.error("AI Summary generation failed:", error);
      alert("Failed to generate AI summary.");
    } finally {
      setSuggestionLoading(false);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFilters((prev) => ({ ...prev, [name]: value }));
  };

  const handleClearFilters = () => {
    setFilters({ ...initialFilters, view: filters.view });
  };

  const handleViewToggle = (view) => {
    setFilters({ ...initialFilters, view });
  };

  const renderFilterInput = (key, options) => {
    const label = key.replace(/_/g, " ");
    if (Array.isArray(options)) {
      return (
        <div style={styles.filterGroup} key={key}>
          <label style={styles.label}>{label}</label>
          <select
            name={key}
            value={filters[key]}
            onChange={handleChange}
            style={styles.select}
          >
            <option value="">All {label}</option>
            {options.map((option) => (
              <option key={option} value={option}>
                {key === "RAM" ? `${option} GB` : option}
              </option>
            ))}
          </select>
        </div>
      );
    } else if (options && typeof options === "object" && "min" in options) {
      const minKey = `min_${key}`;
      const maxKey = `max_${key}`;
      return (
        <div style={styles.filterGroup} key={key}>
          <label style={styles.label}>{label}</label>
          <div style={styles.rangeContainer}>
            <input
              type="number"
              name={minKey}
              value={filters[minKey]}
              onChange={handleChange}
              placeholder={`Min ${options.min}`}
              style={styles.input}
            />
            <input
              type="number"
              name={maxKey}
              value={filters[maxKey]}
              onChange={handleChange}
              placeholder={`Max ${options.max}`}
              style={styles.input}
            />
          </div>
        </div>
      );
    }
    return null;
  };

  if (optionsLoading)
    return <div style={styles.loading}>Loading Filter Options...</div>;

  return (
    <div style={styles.wrapper}>
      {/* THE ONLY HEADER BAR - KEPT AS REQUESTED */}
      <div style={styles.topBar}>
        <p style={styles.userGreeting}>Welcome, {userName} ðŸ‘‹</p>
        <div>
          <button style={styles.logoutButton} onClick={handleLogout}>
            Logout
          </button>
          <button 
             style={styles.aiSummaryButton} 
             onClick={handleAISummary}
             disabled={suggestionLoading}
          >
            {suggestionLoading ? "Loading..." : "AI Summary"}
          </button>
        </div>
      </div>

      <div style={styles.content}>
        <div style={styles.sidebar}>
          <h2 style={styles.title}>Mobile Filters</h2>
          <div style={styles.toggleContainer}>
            <button
              style={{
                ...styles.toggleButton,
                background: filters.view === "all" ? styles.activeGradient : "#aaa",
              }}
              onClick={() => handleViewToggle("all")}
            >
              View All
            </button>
            <button
              style={{
                ...styles.toggleButton,
                background: filters.view === "favorites" ? styles.activeGradient : "#aaa",
              }}
              onClick={() => handleViewToggle("favorites")}
            >
              Favorites ({likedIds.size})
            </button>
          </div>

          {filters.view === "all" && (
            <>
              <div style={styles.filterGroup}>
                <label style={styles.label}>Search Model Name</label>
                <input
                  type="text"
                  name="Model"
                  value={filters.Model}
                  onChange={handleChange}
                  placeholder="e.g., iPhone 16 or Galaxy"
                  style={styles.input}
                />
              </div>
              {Object.entries(filterOptions).map(([key, options]) =>
                renderFilterInput(key, options)
              )}
              <button onClick={handleClearFilters} style={styles.clearButton}>
                Clear All Filters
              </button>
            </>
          )}
        </div>

        <div style={styles.main}>
        <h1 style={styles.header}>
            {filters.view === "favorites"
              ? "Your Favorite Mobiles"
              : "Mobile Catalog"}
          </h1>
          <p style={styles.resultCount}>
            Showing {loading ? "..." : mobiles.length} results
          </p>

          {loading ? (
            <div style={styles.loading}>Loading Data...</div>
          ) : (
            <div style={styles.cardGrid}>
              {mobiles.map((mobile) => {
                const isLiked = likedIds.has(mobile.id);
                return (
                  <div key={mobile.id} style={styles.card}>
                    <div style={styles.cardHeader}>
                      <div>
                        <h3 style={styles.model}>{mobile.Model}</h3>
                        <p style={styles.brand}>{mobile.Brand}</p>
                      </div>
                      <div style={styles.priceTag}>
                        â‚¹{mobile.Price.toLocaleString("en-IN")}
                      </div>
                    </div>

                    <div style={styles.cardBody}>
                      <p><b>Processor:</b> {mobile.Processor}</p>
                      <p><b>RAM:</b> {mobile.RAM} GB</p>
                      <p><b>Battery:</b> {mobile.Battery_Capacity} mAh</p>
                      <p><b>Screen:</b> {mobile.Screen_Size} in</p>
                      <p><b>Front Cam:</b> {mobile.Front_Camera}</p>
                      <p><b>Back Cam:</b> {mobile.Back_Camera}</p>
                      <p><b>Weight:</b> {mobile.Mobile_Weight} g</p>
                      <p><b>Year:</b> {mobile.Launched_Year}</p>
                    </div>

                    <button
                      style={styles.likeButton(isLiked)}
                      onClick={() => handleLikeToggle(mobile.id)}
                    >
                      {isLiked ? "ðŸ’– Liked" : "â™¡ Like"}
                    </button>
                  </div>
                );
              })}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

const styles = {
  activeGradient:
    "linear-gradient(135deg, #f58529, #dd2a7b, #8134af, #515bd4)",

  wrapper: {
    background: "linear-gradient(135deg, #fdfbfb 0%, #ebedee 100%)",
    fontFamily: "'Inter', sans-serif",
    minHeight: "100vh",
  },

  // THE REQUESTED HEADER BAR
  topBar: {
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
    padding: "20px 40px",
    backgroundColor: "#fff",
    boxShadow: "0 4px 12px rgba(0,0,0,0.05)",
    borderBottom: "1px solid #eee",
  },
  userGreeting: {
    fontSize: "20px",
    fontWeight: "600",
    color: "#333",
  },
  logoutButton: {
    marginRight: "10px",
    backgroundColor: "#e1306c",
    color: "white",
    border: "none",
    padding: "10px 20px",
    borderRadius: "8px",
    fontWeight: "600",
    cursor: "pointer",
    transition: "all 0.3s ease",
  },
  aiSummaryButton: {
    backgroundColor: "#8134af",
    color: "white",
    border: "none",
    padding: "10px 20px",
    borderRadius: "8px",
    fontWeight: "600",
    cursor: "pointer",
    transition: "all 0.3s ease",
  },

  content: {
    display: "flex",
    gap: "20px",
    padding: "20px 20px 40px 20px", // Added padding top here
  },
  sidebar: {
    width: "300px",
    backgroundColor: "white",
    borderRadius: "20px",
    padding: "25px",
    boxShadow: "0 4px 20px rgba(0,0,0,0.1)",
  },
  main: {
    flexGrow: 1,
    backgroundColor: "white",
    borderRadius: "20px",
    padding: "30px",
    boxShadow: "0 4px 20px rgba(0,0,0,0.1)",
  },
  title: { fontSize: "22px", fontWeight: "700", marginBottom: "20px" },
  toggleContainer: {
    display: "flex",
    flexDirection: "column",
    gap: "10px",
    marginBottom: "25px",
  },
  toggleButton: {
    color: "#fff",
    border: "none",
    padding: "12px",
    borderRadius: "30px",
    cursor: "pointer",
    fontWeight: "600",
    transition: "0.3s",
  },
  filterGroup: { marginBottom: "18px" },
  label: {
    fontWeight: "600",
    fontSize: "13px",
    marginBottom: "5px",
    display: "block",
  },
  input: {
    width: "100%",
    padding: "10px",
    borderRadius: "12px",
    border: "1px solid #ddd",
    outline: "none",
  },
  select: {
    width: "100%",
    padding: "10px",
    borderRadius: "12px",
    border: "1px solid #ddd",
  },
  rangeContainer: { display: "flex", gap: "10px" },
  clearButton: {
    marginTop: "15px",
    background: "linear-gradient(135deg, #ff5f6d, #ffc371)",
    color: "white",
    border: "none",
    padding: "12px",
    borderRadius: "30px",
    fontWeight: "600",
    cursor: "pointer",
  },
  header: { fontSize: "26px", fontWeight: "700", marginBottom: "10px" },
  resultCount: { color: "#888", marginBottom: "25px" },
  cardGrid: {
    display: "grid",
    gridTemplateColumns: "repeat(auto-fill, minmax(280px, 1fr))",
    gap: "25px",
  },
  card: {
    backgroundColor: "white",
    borderRadius: "20px",
    boxShadow: "0 6px 20px rgba(0,0,0,0.08)",
    padding: "20px",
    transition: "transform 0.3s ease, box-shadow 0.3s ease",
  },
  cardHeader: {
    display: "flex",
    justifyContent: "space-between",
    alignItems: "flex-start",
    marginBottom: "12px",
  },
  model: { fontSize: "18px", margin: 0 },
  brand: { color: "#999", marginTop: "4px" },
  priceTag: {
    background: "linear-gradient(135deg, #f58529, #dd2a7b)",
    color: "white",
    padding: "6px 10px",
    borderRadius: "12px",
    fontWeight: "600",
  },
  cardBody: { fontSize: "14px", color: "#333", lineHeight: "1.6" },
  likeButton: (isLiked) => ({
    marginTop: "10px",
    alignSelf: "flex-end",
    border: "none",
    borderRadius: "30px",
    padding: "8px 16px",
    cursor: "pointer",
    color: isLiked ? "#fff" : "#e1306c",
    background: isLiked
      ? "linear-gradient(135deg, #f58529, #dd2a7b, #8134af)"
      : "#f8f9fa",
    fontWeight: "bold",
    transition: "all 0.3s ease",
  }),
};

export default MobileFilter;
