import React, { useState, useEffect } from "react";
import axios from "axios";

const API_BASE_URL = "http://localhost:3001/api/mobile";
const USER_API_URL = "http://localhost:3001/api/user"; 
const AI_SUGGESTION_API = "http://localhost:3001/api/suggest"; // <<< New API endpoint

const initialFilters = {
  Brand: "",
  RAM: "",
  Front_Camera: "",
  Back_Camera: "",
  Processor: "",
  Launched_Year: "",
  min_Price: "",
  max_Price: "",
  min_Battery_Capacity: "",
  max_Battery_Capacity: "",
  min_Mobile_Weight: "",
  max_Mobile_Weight: "",
  min_Screen_Size: "",
  max_Screen_Size: "",
  Model: "",
  view: "all",
};

const MobileFilter = ({ userID, onLogout }) => {
  const [filters, setFilters] = useState(initialFilters);
  const [mobiles, setMobiles] = useState([]);
  const [filterOptions, setFilterOptions] = useState({});
  const [loading, setLoading] = useState(false);
  const [optionsLoading, setOptionsLoading] = useState(true);
  const [likedIds, setLikedIds] = useState(new Set());
  const [userName, setUserName] = useState("");
  // New state to manage suggestion results
  const [suggestionResult, setSuggestionResult] = useState(null); 
  const [suggestionLoading, setSuggestionLoading] = useState(false);

  // ... (useEffect for fetchUserName, fetchLikedIds, fetchOptions remain the same) ...
  // ... (useEffect for fetching filtered data remains the same) ...

  const handleAISuggestion = async () => {
    // 1. Only run this if we are in 'all' view and not currently loading suggestions
    if (filters.view !== "all" || suggestionLoading) return;

    setSuggestionLoading(true);
    setSuggestionResult(null); // Clear previous suggestions

    try {
      // 2. Extract only the active filter criteria to send to the backend
      const { view, ...restFilters } = filters;
      const activeFilters = Object.fromEntries(
        Object.entries(restFilters).filter(([_, v]) => v !== "")
      );

      if (Object.keys(activeFilters).length === 0) {
        alert("Please select at least one filter before getting AI suggestions!");
        setSuggestionLoading(false);
        return;
      }

      // 3. Send the active filters to the new backend AI route
      const queryParams = new URLSearchParams(activeFilters).toString();
      const response = await axios.get(`${AI_SUGGESTION_API}?${queryParams}`);

      // 4. Update the UI to show the suggested mobiles
      setMobiles(response.data); 
      setSuggestionResult(`Showing AI's top ${response.data.length} suggestions.`);

    } catch (error) {
      console.error("AI Suggestion failed:", error);
      setSuggestionResult("AI suggestion failed. Try again later.");
    } finally {
      setSuggestionLoading(false);
    }
  };

  // ... (handleLikeToggle, handleLogout, handleChange, handleClearFilters, handleViewToggle, renderFilterInput remain the same) ...

  if (optionsLoading)
    return <div style={styles.loading}>Loading Filter Options...</div>;

  return (
    <div style={styles.wrapper}>

      {/* Main Welcome Banner - (This must be the top element in your main app structure) */}
      <div style={styles.welcomeBanner}>
        {/* ... Banner content remains the same ... */}
        <h1 style={styles.welcomeTitle}>
          Welcome back,{" "}
          <span style={styles.userName}>
            {loading || !userName ? "Loading..." : userName}
          </span>{" "}
          ðŸ‘‹
        </h1>
        <p style={styles.welcomeSubtitle}>
          Explore the latest mobiles curated for you!
        </p>
        <button style={styles.logoutButton} onClick={handleLogout}>
          Logout
        </button>
      </div>

      {/* Filters + Catalog */}
      <div style={styles.content}>
        <div style={styles.sidebar}>
          <h2 style={styles.title}>Mobile Filters</h2>
          {/* ... View Toggle buttons remain the same ... */}
          <div style={styles.toggleContainer}>
            <button
              style={{
                ...styles.toggleButton,
                background: filters.view === "all" ? styles.activeGradient : "#aaa",
              }}
              onClick={() => handleViewToggle("all")}
            >
              View All
            </button>
            <button
              style={{
                ...styles.toggleButton,
                background:
                  filters.view === "favorites" ? styles.activeGradient : "#aaa",
              }}
              onClick={() => handleViewToggle("favorites")}
            >
              Favorites ({likedIds.size})
            </button>
          </div>

          {filters.view === "all" && (
            <>
              {/* ... Search Model Name and Filters ... */}
              <div style={styles.filterGroup}>
                <label style={styles.label}>Search Model Name</label>
                <input
                  type="text"
                  name="Model"
                  value={filters.Model}
                  onChange={handleChange}
                  placeholder="e.g., iPhone 16 or Galaxy"
                  style={styles.input}
                />
              </div>
              {Object.entries(filterOptions).map(([key, options]) =>
                renderFilterInput(key, options)
              )}

              {/* Clear Filters Button */}
              <button onClick={handleClearFilters} style={styles.clearButton}>
                Clear All Filters
              </button>

              {/* AI Suggestion Button (NEW) */}
              <button 
                onClick={handleAISuggestion} 
                style={styles.aiSuggestionButton}
                disabled={suggestionLoading || filters.view !== "all"}
              >
                {suggestionLoading ? "Thinking..." : "âœ¨ Get AI Suggestion"}
              </button>
            </>
          )}
        </div>

        <div style={styles.main}>
          <h1 style={styles.header}>
            {filters.view === "favorites"
              ? "Your Favorite Mobiles"
              : suggestionResult || "Mobile Catalog"} {/* Display suggestion status here */}
          </h1>
          <p style={styles.resultCount}>
            Showing {loading || suggestionLoading ? "..." : mobiles.length} results
          </p>

          {/* ... Mobile Cards Grid remains the same ... */}
          {loading || suggestionLoading ? (
            <div style={styles.loading}>Loading Data...</div>
          ) : (
             <div style={styles.cardGrid}>
                {mobiles.map((mobile) => {
                  const isLiked = likedIds.has(mobile.id);
                  return (
                    <div key={mobile.id} style={styles.card}>
                      {/* ... mobile card details ... */}
                      <div style={styles.cardHeader}>
                        <div>
                          <h3 style={styles.model}>{mobile.Model}</h3>
                          <p style={styles.brand}>{mobile.Brand}</p>
                        </div>
                        <div style={styles.priceTag}>
                          â‚¹{mobile.Price.toLocaleString("en-IN")}
                        </div>
                      </div>
                      <div style={styles.cardBody}>
                        <p><b>Processor:</b> {mobile.Processor}</p>
                        <p><b>RAM:</b> {mobile.RAM} GB</p>
                        {/* ... other specs ... */}
                      </div>
                      <button
                        style={styles.likeButton(isLiked)}
                        onClick={() => handleLikeToggle(mobile.id)}
                      >
                        {isLiked ? "ðŸ’– Liked" : "â™¡ Like"}
                      </button>
                    </div>
                  );
                })}
              </div>
          )}
        </div>
      </div>
    </div>
  );
};

const styles = {
  // ... (Existing styles remain the same) ...
  activeGradient:
    "linear-gradient(135deg, #f58529, #dd2a7b, #8134af, #515bd4)",

  // New Style for AI Suggestion Button
  aiSuggestionButton: {
    marginTop: "10px", 
    background: "linear-gradient(135deg, #4CAF50, #81C784)", // Green Gradient
    color: "white",
    border: "none",
    padding: "12px",
    borderRadius: "30px",
    fontWeight: "600",
    cursor: "pointer",
    width: "100%",
    transition: "0.3s",
  },

  // ... (Rest of the styles remain the same) ...
  wrapper: { /* ... */ },
  welcomeBanner: { /* ... */ },
  // ... and so on ...
};

export default MobileFilter;
