import React, { useState, useEffect } from 'react';
import axios from 'axios';

const API_BASE_URL = 'http://localhost:3001/api/mobile';

const initialFilters = {
  Brand: '',
  RAM: '',
  Front_Camera: '',
  Back_Camera: '',
  Processor: '',
  Launched_Year: '',
  min_Price: '',
  max_Price: '',
  min_Battery_Capacity: '',
  max_Battery_Capacity: '',
  min_Mobile_Weight: '',
  max_Mobile_Weight: '',
  min_Screen_Size: '',
  max_Screen_Size: '',
  Model: '',
  view: 'all'
};

const styles = {
  container: {
    display: 'flex',
    fontFamily: 'Roboto, sans-serif',
    minHeight: '100vh',
    backgroundColor: '#f8f9fa',
  },
  sidebar: {
    padding: '25px',
    borderRight: '1px solid #dee2e6',
    width: '320px',
    minWidth: '300px',
    backgroundColor: '#ffffff',
    boxShadow: '2px 0 5px rgba(0,0,0,0.05)',
    overflowY: 'auto',
  },
  resultsArea: {
    flexGrow: 1,
    padding: '25px',
    backgroundColor: '#f8f9fa',
    overflowX: 'auto',
  },
  filterGroup: {
    marginBottom: '20px',
    padding: '10px',
    border: '1px solid #e9ecef',
    borderRadius: '8px',
    backgroundColor: '#f9f9f9',
  },
  label: {
    display: 'block',
    fontWeight: '600',
    marginBottom: '5px',
    color: '#495057',
    fontSize: '14px',
  },
  input: {
    width: '100%',
    padding: '8px',
    marginBottom: '10px',
    border: '1px solid #ced4da',
    borderRadius: '4px',
    boxSizing: 'border-box',
  },
  select: {
    width: '100%',
    padding: '8px',
    marginBottom: '10px',
    border: '1px solid #ced4da',
    borderRadius: '4px',
    boxSizing: 'border-box',
  },
  rangeContainer: {
    display: 'flex',
    gap: '10px',
    marginBottom: '10px',
  },
  table: {
    minWidth: '1300px',
    borderCollapse: 'collapse',
    marginTop: '20px',
    backgroundColor: '#ffffff',
    boxShadow: '0 0 10px rgba(0,0,0,0.05)',
    borderRadius: '8px',
    overflow: 'hidden',
  },
  th: {
    backgroundColor: '#343a40',
    color: '#ffffff',
    padding: '12px 10px',
    textAlign: 'left',
    fontSize: '14px',
    textTransform: 'uppercase',
  },
  td: {
    padding: '12px 10px',
    borderBottom: '1px solid #f2f2f2',
    fontSize: '13px',
    color: '#343a40',
    whiteSpace: 'nowrap',
    overflow: 'hidden',
    textOverflow: 'ellipsis',
    maxWidth: '150px',
  },
  tr: {
    transition: 'background-color 0.3s ease',
  },
  button: {
    backgroundColor: '#dc3545',
    color: 'white',
    border: 'none',
    padding: '10px 15px',
    borderRadius: '4px',
    cursor: 'pointer',
    width: '100%',
    marginTop: '20px',
    fontWeight: 'bold',
    transition: 'background-color 0.3s ease',
  },
  favoriteButton: {
    backgroundColor: '#007bff',
    marginBottom: '10px',
  },
  likeButton: (isLiked) => ({
    cursor: 'pointer',
    padding: '5px 8px',
    borderRadius: '4px',
    border: '1px solid',
    borderColor: isLiked ? '#dc3545' : '#ccc',
    backgroundColor: isLiked ? '#dc3545' : '#f8f9fa',
    color: isLiked ? 'white' : '#dc3545',
    fontWeight: 'bold',
    transition: 'all 0.2s ease',
    fontSize: '12px',
    whiteSpace: 'nowrap',
  }),
};

const MobileFilter = ({ userID }) => {
  const [filters, setFilters] = useState(initialFilters);
  const [mobiles, setMobiles] = useState([]);
  const [filterOptions, setFilterOptions] = useState({});
  const [loading, setLoading] = useState(false);
  const [optionsLoading, setOptionsLoading] = useState(true);
  const [likedIds, setLikedIds] = useState(new Set());

  // Fetch liked mobiles for this user
  const fetchLikedIds = async () => {
    if (!userID) return;
    try {
      const response = await axios.get(`${API_BASE_URL}/favorites`, {
        params: { userID },
      });
      const ids = new Set(response.data.map((m) => m.id));
      setLikedIds(ids);
    } catch (error) {
      console.error('Failed to fetch liked IDs:', error);
    }
  };

  // Fetch filter options
  useEffect(() => {
    const fetchOptions = async () => {
      try {
        const response = await axios.get(`${API_BASE_URL}/options`);
        setFilterOptions(response.data);
      } catch (error) {
        console.error('Error fetching filter options:', error);
      } finally {
        setOptionsLoading(false);
      }
    };
    fetchOptions();
    fetchLikedIds();
  }, [userID]);

  // Fetch data when filters change
  useEffect(() => {
    if (optionsLoading) return;

    const fetchData = async () => {
      setLoading(true);
      try {
        let response;
        if (filters.view === 'favorites') {
          response = await axios.get(`${API_BASE_URL}/favorites`, {
            params: { userID },
          });
        } else {
          const { view, ...restFilters } = filters;
          const activeFilters = Object.fromEntries(
            Object.entries(restFilters).filter(([_, v]) => v !== '')
          );
          const queryParams = new URLSearchParams(activeFilters).toString();
          response = await axios.get(`${API_BASE_URL}/filter?${queryParams}`);
        }
        setMobiles(response.data);
      } catch (error) {
        console.error('Error fetching data:', error);
      } finally {
        setLoading(false);
      }
    };

    const handler = setTimeout(() => fetchData(), 300);
    return () => clearTimeout(handler);
  }, [filters, optionsLoading, userID]);

  // Like/Unlike handler
  const handleLikeToggle = async (mobileId) => {
    const isLiked = likedIds.has(mobileId);
    try {
      const response = await axios.post(`${API_BASE_URL}/like`, {
        mobileId,
        userID,
        isLiked: !isLiked,
      });
      if (response.data.success) {
        setLikedIds((prev) => {
          const newIds = new Set(prev);
          isLiked ? newIds.delete(mobileId) : newIds.add(mobileId);
          return newIds;
        });
        if (filters.view === 'favorites') setFilters((prev) => ({ ...prev }));
      }
    } catch (error) {
      console.error('Toggle like failed:', error);
      alert('Failed to update like status.');
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFilters((prev) => ({ ...prev, [name]: value }));
  };

  const handleClearFilters = () => {
    setFilters({ ...initialFilters, view: filters.view });
  };

  const handleViewToggle = (view) => {
    setFilters({ ...initialFilters, view });
  };

  const renderFilterInput = (key, options) => {
    const label = key.replace(/_/g, ' ');
    if (Array.isArray(options)) {
      return (
        <div style={styles.filterGroup} key={key}>
          <label style={styles.label}>{label}:</label>
          <select
            name={key}
            value={filters[key]}
            onChange={handleChange}
            style={styles.select}
          >
            <option value="">All {label}</option>
            {options.map((option) => (
              <option key={option} value={option}>
                {key === 'RAM' ? `${option} GB` : option}
              </option>
            ))}
          </select>
        </div>
      );
    } else if (options && typeof options === 'object' && 'min' in options) {
      const minKey = `min_${key}`;
      const maxKey = `max_${key}`;
      return (
        <div style={styles.filterGroup} key={key}>
          <label style={styles.label}>{label}:</label>
          <div style={styles.rangeContainer}>
            <input
              type="number"
              name={minKey}
              value={filters[minKey]}
              onChange={handleChange}
              placeholder={`Min ${options.min}`}
              style={styles.input}
            />
            <input
              type="number"
              name={maxKey}
              value={filters[maxKey]}
              onChange={handleChange}
              placeholder={`Max ${options.max}`}
              style={styles.input}
            />
          </div>
        </div>
      );
    }
    return null;
  };

  if (optionsLoading)
    return <div style={{ padding: '20px' }}>Loading Filter Options...</div>;

  return (
    <div style={styles.container}>
      {/* Sidebar */}
      <div style={styles.sidebar}>
        <h2>Mobile Filters</h2>

        {/* View toggle */}
        <div
          style={{
            ...styles.filterGroup,
            padding: '15px',
            display: 'flex',
            flexDirection: 'column',
          }}
        >
          <button
            style={{
              ...styles.favoriteButton,
              backgroundColor: filters.view === 'all' ? '#0056b3' : '#6c757d',
              color: 'white',
            }}
            onClick={() => handleViewToggle('all')}
          >
            View All Mobiles
          </button>
          <button
            style={{
              ...styles.favoriteButton,
              backgroundColor:
                filters.view === 'favorites' ? '#0056b3' : '#6c757d',
              color: 'white',
            }}
            onClick={() => handleViewToggle('favorites')}
          >
            View {likedIds.size} Liked Mobiles
          </button>
        </div>

        {filters.view === 'all' && (
          <>
            <div style={styles.filterGroup}>
              <label style={styles.label}>Search Model Name:</label>
              <input
                type="text"
                name="Model"
                value={filters.Model}
                onChange={handleChange}
                placeholder="e.g., iPhone 16 or Galaxy"
                style={styles.input}
              />
            </div>

            {Object.entries(filterOptions).map(([key, options]) =>
              renderFilterInput(key, options)
            )}

            <button onClick={handleClearFilters} style={styles.button}>
              Clear All Filters
            </button>
          </>
        )}
      </div>

      {/* Results */}
      <div style={styles.resultsArea}>
        <h1>
          {filters.view === 'favorites'
            ? 'Your Favorite Mobiles'
            : 'Mobile Catalog'}
        </h1>
        <h3>Showing {loading ? '...' : mobiles.length} Results</h3>

        {loading ? (
          <div style={{ color: '#007bff', fontWeight: 'bold' }}>
            Loading Data...
          </div>
        ) : (
          <table style={styles.table}>
            <thead>
              <tr>
                <th style={styles.th}>Action</th>
                <th style={styles.th}>Brand</th>
                <th style={styles.th}>Model</th>
                <th style={styles.th}>Price (INR)</th>
                <th style={styles.th}>RAM</th>
                <th style={styles.th}>Processor</th>
                <th style={styles.th}>Battery</th>
                <th style={styles.th}>Screen</th>
                <th style={styles.th}>Weight</th>
                <th style={styles.th}>Front Cam</th>
                <th style={styles.th}>Back Cam</th>
                <th style={styles.th}>Year</th>
              </tr>
            </thead>
            <tbody>
              {mobiles.length === 0 ? (
                <tr>
                  <td
                    colSpan="12"
                    style={{
                      ...styles.td,
                      textAlign: 'center',
                      color: '#dc3545',
                    }}
                  >
                    {filters.view === 'favorites'
                      ? 'You have no liked mobiles yet.'
                      : 'No mobiles match your current filter criteria.'}
                  </td>
                </tr>
              ) : (
                mobiles.map((mobile) => {
                  const isLiked = likedIds.has(mobile.id);
                  return (
                    <tr
                      key={mobile.id || `${mobile.Brand}-${mobile.Model}`}
                      style={styles.tr}
                    >
                      <td style={styles.td}>
                        <button
                          style={styles.likeButton(isLiked)}
                          onClick={() => handleLikeToggle(mobile.id)}
                          title={
                            isLiked
                              ? 'Unlike this mobile'
                              : 'Add to favorites'
                          }
                        >
                          {isLiked ? '❤️ Liked' : '☆ Like'}
                        </button>
                      </td>
                      <td style={styles.td}>{mobile.Brand}</td>
                      <td style={styles.td}>{mobile.Model}</td>
                      <td style={styles.td}>
                        ₹{mobile.Price.toLocaleString('en-IN')}
                      </td>
                      <td style={styles.td}>{mobile.RAM} GB</td>
                      <td style={styles.td}>{mobile.Processor}</td>
                      <td style={styles.td}>{mobile.Battery_Capacity} mAh</td>
                      <td style={styles.td}>{mobile.Screen_Size} in</td>
                      <td style={styles.td}>{mobile.Mobile_Weight} g</td>
                      <td style={styles.td}>{mobile.Front_Camera}</td>
                      <td style={styles.td}>{mobile.Back_Camera}</td>
                      <td style={styles.td}>{mobile.Launched_Year}</td>
                    </tr>
                  );
                })
              )}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
};

export default MobileFilter;
