import React, { useState, useEffect } from 'react';
import axios from 'axios';

const API_BASE_URL = 'http://localhost:3001/api/mobile';

// Initial state for filters, covering all fields
const initialFilters = {
    // Dropdowns
    Brand: '',
    RAM_Size: '',
    Front_Camera: '',
    Back_Camera: '',
    Processor: '',
    Launched_Year: '',
    // Range inputs (min/max)
    min_Price: '', max_Price: '',
    min_Battery_Capacity: '', max_Battery_Capacity: '',
    min_Mobile_Weight: '', max_Mobile_Weight: '',
    min_Screen_Size: '', max_Screen_Size: '',
    // Search
    Model: ''
};

const MobileFilter = () => {
    const [filters, setFilters] = useState(initialFilters);
    const [mobiles, setMobiles] = useState([]);
    const [filterOptions, setFilterOptions] = useState({});
    const [loading, setLoading] = useState(false);
    const [optionsLoading, setOptionsLoading] = useState(true);

    // --- Fetch Filter Options on Component Mount ---
    useEffect(() => {
        const fetchOptions = async () => {
            try {
                const response = await axios.get(`${API_BASE_URL}/options`);
                setFilterOptions(response.data);
            } catch (error) {
                console.error('Error fetching filter options:', error);
            } finally {
                setOptionsLoading(false);
            }
        };
        fetchOptions();
    }, []);

    // --- Fetch Filtered Data (Debounced) ---
    useEffect(() => {
        if (optionsLoading) return;

        const fetchData = async () => {
            setLoading(true);
            try {
                // Remove empty keys from the filters state before creating query params
                const activeFilters = Object.fromEntries(
                    Object.entries(filters).filter(([_, v]) => v !== '')
                );
                const queryParams = new URLSearchParams(activeFilters).toString();

                const response = await axios.get(`${API_BASE_URL}/filter?${queryParams}`);
                setMobiles(response.data);
            } catch (error) {
                console.error('Error fetching data:', error);
            } finally {
                setLoading(false);
            }
        };

        // Debounce: Wait 300ms before fetching
        const handler = setTimeout(() => {
            fetchData();
        }, 300); 

        return () => {
            clearTimeout(handler);
        };
    }, [filters, optionsLoading]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFilters(prev => ({
            ...prev,
            [name]: value
        }));
    };

    const handleClearFilters = () => {
        setFilters(initialFilters);
    };

    const renderFilterInput = (key, options) => {
        // Dropdown (Select) Filter
        if (Array.isArray(options)) {
            return (
                <div key={key}>
                    <label htmlFor={key}>{key.replace(/_/g, ' ')}:</label>
                    <select id={key} name={key} value={filters[key]} onChange={handleChange}>
                        <option value="">All {key.replace(/_/g, ' ')}</option>
                        {options.map(option => (
                            <option key={option} value={option}>{option}</option>
                        ))}
                    </select>
                </div>
            );
        } 

        // Range (Min/Max) Filter
        else if (options && typeof options === 'object' && 'min' in options && 'max' in options) {
            const label = key.replace(/_/g, ' ');
            const minKey = `min_${key}`;
            const maxKey = `max_${key}`;
            return (
                <div key={key} style={{ marginTop: '15px' }}>
                    <label>{label}:</label>
                    <input
                        type="number"
                        name={minKey}
                        value={filters[minKey]}
                        onChange={handleChange}
                        placeholder={`Min ${label} (${options.min})`}
                    />
                    <input
                        type="number"
                        name={maxKey}
                        value={filters[maxKey]}
                        onChange={handleChange}
                        placeholder={`Max ${label} (${options.max})`}
                    />
                </div>
            );
        }
        return null;
    };


    if (optionsLoading) return <div>Loading Filter Options...</div>;

    return (
        <div style={{ display: 'flex', fontFamily: 'Arial' }}>
            {/* Filter Sidebar */}
            <div style={{ padding: '20px', borderRight: '1px solid #ccc', width: '300px' }}>
                <h3>Filter Options</h3>

                {/* Search Field (Model) */}
                <div>
                    <label htmlFor="Model">Search Model:</label>
                    <input
                        type="text"
                        name="Model"
                        id="Model"
                        value={filters.Model}
                        onChange={handleChange}
                        placeholder="Search model names..."
                    />
                </div>

                {/* Dynamically Render Dropdown and Range Filters */}
                {Object.entries(filterOptions).map(([key, options]) => renderFilterInput(key, options))}

                <button onClick={handleClearFilters} style={{ marginTop: '20px', padding: '10px' }}>
                    Clear All Filters
                </button>
            </div>

            {/* Results Table */}
            <div style={{ flexGrow: 1, padding: '20px' }}>
                <h2>Mobile Results ({loading ? '...' : mobiles.length})</h2>
                {loading ? (
                    <p>Loading Data...</p>
                ) : (
                    <table style={{ width: '100%', borderCollapse: 'collapse' }}>
                        <thead>
                            <tr style={{ backgroundColor: '#f2f2f2' }}>
                                <th>Brand</th>
                                <th>Model</th>
                                <th>Price</th>
                                <th>RAM</th>
                                <th>Processor</th>
                                <th>Battery</th>
                                <th>Weight</th>
                            </tr>
                        </thead>
                        <tbody>
                            {mobiles.map(mobile => (
                                <tr key={mobile.id || `${mobile.Brand}-${mobile.Model}`} style={{ borderBottom: '1px solid #eee' }}>
                                    <td>{mobile.Brand}</td>
                                    <td>{mobile.Model}</td>
                                    <td>${mobile.Price}</td>
                                    <td>{mobile.RAM_Size}</td>
                                    <td>{mobile.Processor}</td>
                                    <td>{mobile.Battery_Capacity}mAh</td>
                                    <td>{mobile.Mobile_Weight}g</td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                )}
            </div>
        </div>
    );
};

export default MobileFilter;
